void ssd1306_display_char(uint8_t x, uint8_t y, uint8_t ch)
{
    uint8_t i, j;
    uint8_t temp, ypos0 = y;

    ch = ch - ' ';
    for (i = 0; i < 64; i++)
    {
        temp = Font1608[ch][i];

        for (j = 0; j < 8; j++)
        {
            if (temp & 0x80)
            {
                ssd1306_draw_point(x, y, 1);
            }
            else
            {
                ssd1306_draw_point(x, y, 0);
            }

            temp <<= 1;
            y++;

            if ((y - ypos0) == 32)
            {
                y = ypos0;
                x++;
                break;
            }
        }
    }
}

